def lowUlow(string):
    p = 0
    llist = []
    for i in range(len(string)):
        if p < 996:
            p +=1
        if string[i].islower() == string[p].islower():
            continue
        if string[i].islower():
            llist.append(string[i])
        else:
            llist.append(string[p])
    return llist


print('Use own func')
print(lowUlow(line))
print(len(lowUlow(line)))
