def lowUlow(string):
    p = 0
    llist = []
    for i in range(len(string)):
        if p < len(string) - 1:
            p += 1
        if p < len(string) and string[i].islower() == string[p].islower():
            continue
        if string[i].islower():
            llist.append(string[i])
        else:
            llist.append(string[p])
    return llist


print('Use my own function')
print(lowUlow(line))
print('Total number is: ', len(lowUlow(line)))
