import os

line = ''


def write2file(l):
    path = makePath()
    char = 0
    for num in range(1, 6):
        file = open(path + '\\file_' + str(num) + '.txt', 'w')
        file.write(l[char])
        file.close()
        char += 1


def makePath():
    if os.path.exists('c:\\'):
        path = 'c:\\Tranio'
        os.makedirs(path)
    else:
        path = 'd:\\Tranio'
        os.makedirs(path)
    return path


write2file(line)
