import os

line = ''


def write2file(l):
    path = makePath()
    for i in range(5):
        num = i + 1
        file = open(path + '\\file_' + str(num) + '.txt', 'w')
        file.write(str([i for i in l[i::5]]))
        file.close()


def makePath():
    pathC = 'c:\\Tranio'
    if not os.path.exists(pathC):
        os.makedirs(pathC)
        return pathC


write2file(line)
