import os

line = ''


def write2file(l):
    path = makePath()
    char = 0
    for num in range(1, 6):
        file = open(path + '\\file_' + str(num) + '.txt', 'w')
        file.write(l[char])
        file.close()
        char += 1


def makePath():
    pathC = 'c:\\Tranio'
    if not os.path.exists(pathC):
        os.makedirs(pathC)
        return pathC


write2file(line)
